import React from "react";
import { useAxios } from "../../custom-hooks";
import "./table.css";

const Table = () => {
  const columns = [
    "브랜드",
    "제조원",
    "수질적합여부",
    "부적합판정횟수",
    "출시일",
  ];

  //api 호출
  const { waterBrands, waterSources, brandSourceMappings, testHistory } =
    useAxios();

  //데이터 편집
  console.log(waterBrands);

  return (
    <div className="tableArea">
      <div className="tableWrapper">
        <table className="tableHead">
          <thead>
            <tr>
              {columns.map((column, index) => (
                <th key={index}>{column}</th>
              ))}
            </tr>
          </thead>
        </table>
        <table className="tableBody">
          <tbody>
            {waterBrands.map((waterBrand, index) => {
              const waterBrandId = waterBrand.id;
              console.log(typeof waterBrandId);
              const waterSourceIds = brandSourceMappings
                .filter(
                  (brandSourceMapping) =>
                    brandSourceMapping.brand === waterBrandId
                )
                .map((brandSourceMapping) => brandSourceMapping.source);
              console.log(typeof waterSourceIds);
              const waterSourceList = waterSourceIds.map(
                (id) => waterSources[id - 2]
              );
              console.log(waterSourceList);
              const invalidCount = testHistory.filter((hist) =>
                waterSourceIds.includes(hist.source)
              ).length;
              console.log(invalidcount)
              const isValid = invalidCount === 0 ? "O" : "X";

              return (
                <tr key={index}>
                  <td>{waterBrand.name}</td>
                  <td>{[...waterSourceList]}</td>
                  <td>{isValid}</td>
                  <td>{invalidCount}</td>
                  <td>comingSoon</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Table;
