import React, { useState, useEffect, useRef } from "react";
import "./row-slider.css";

const RowSlider = () => {
  const [waterBrands, setWaterBrands] = useState([]);

  useEffect(() => {
    axios.get("http://3.39.164.17:8000/api/watersource/").then((response) => {
      setWaterBrands(response.data);
    });
  }, []);

  const TOTAL_SLIDES = waterBrands.length - 1;
  const [currentSlide, setCurrentSlide] = useState(0);
  const slideRef = useRef(null);
  const nextSlide = () => {
    if (currentSlide >= TOTAL_SLIDES) {
      setCurrentSlide(0);
    } else {
      setCurrentSlide(currentSlide + 1);
    }
  };
  const prevSlide = () => {
    if (currentSlide === 0) {
      setCurrentSlide(TOTAL_SLIDES);
    } else {
      setCurrentSlide(currentSlide - 1);
    }
  };

  useEffect(() => {
    slideRef.current.style.transition = "all 0.5s ease-in-out";
    slideRef.current.style.transform = `translateX(-${currentSlide}00%)`;
  }, [currentSlide]);
  return (
    <div className = 'rowSliderWrapper'>
      {currentSlide}
      <div className = "rowSliderContainer" ref={slideRef}>
        {waterBrands.map((waterBrand, index) => (
            <div className="rowSliderSlide" key={index}>
                <p>{waterBrand.name}</p>
            </div>
        ))}
      </div>
      <button className='rowSliderButton' onClick={prevSlide}>Previous Slide</button>
      <button className='rowSliderButton' onClick={nextSlide}>Next Slide</button>
    </div>
  );
};

export default RowSlider;
