import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "./row-slider.css";

const RowSlider = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const transitionTime1 = 500;
  const transitionTime2 = 450;
  const transitionStyle1 = `transform ${transitionTime}ms ease 0s`;
  const [slideTransition, setSlideTransition] = useState();

  const handleSwipe = (direction) => {
    //뒤로 넘어갈 때
    if (currentIndex + direction === 3) {
      setCurrentIndex(0);
    }
    //앞으로 넘어갈 때
    else if (currentIndex + direction === -1) {
      setCurrentIndex(3 - 1);
    } else {
      setCurrentIndex((currentIndex) => currentIndex + direction);
    }
  };

  useEffect(() => {
    console.log(currentIndex);
  }, [currentIndex]);

  return (
    <div className="rowSliderSliderArea">
      <div className="rowSliderSlider">
        <div className="rowSliderSliderList">
          <div
            className="rowSliderSliderTrack1"
            style={{
              transform: `translate(${
                152 - (128 + 16) * 2 - 128 / 2 - (128 + 16) * currentIndex
              }px, 16px)`,
              transition: "transform 500ms ease 0s",
            }}
          >
            <div
              className="rowSliderItem2"
              style={{
                backgroundColor: "#866262",
                width: "128px",
                height: "128px",
              }}
            >
              <h1>2</h1>
            </div>
            <div
              className="rowSliderItem3"
              style={{
                backgroundColor: "#866262",
                width: "128px",
                height: "128px",
              }}
            >
              <h1>3</h1>
            </div>
            <div
              className="rowSliderItem1"
              style={{
                backgroundColor: "#866262",
                width: "128px",
                height: "128px",
              }}
            >
              <h1>1</h1>
            </div>
            <div
              className="rowSliderItem2"
              style={{
                backgroundColor: "#866262",
                width: "128px",
                height: "128px",
              }}
            >
              <h1>2</h1>
            </div>
            <div
              className="rowSliderItem3"
              style={{
                backgroundColor: "#866262",
                width: "128px",
                height: "128px",
              }}
            >
              <h1>3</h1>
            </div>
            <div
              className="rowSliderItem1"
              style={{
                backgroundColor: "#866262",
                width: "128px",
                height: "128px",
              }}
            >
              <h1>1</h1>
            </div>
            <div
              className="rowSliderItem2"
              style={{
                backgroundColor: "#866262",
                width: "128px",
                height: "128px",
              }}
            >
              <h1>2</h1>
            </div>
          </div>
          <div
            className="rowSliderSliderTrack2"
            style={{
              transform: `translate(${
                152 - (128 + 16) * (2 + 1) + 16 / 2 - (128 + 16) * currentIndex
              }px, 32px)`,
              transition: "transform 450ms ease 0s",
            }}
          >
            <div
              className="rowSliderItem2"
              style={{
                backgroundColor: "#5effb1",
                width: "128px",
                height: "128px",
              }}
            >
              <h1>2</h1>
            </div>
            <div
              className="rowSliderItem3"
              style={{
                backgroundColor: "#5effb1",
                width: "128px",
                height: "128px",
              }}
            >
              <h1>3</h1>
            </div>
            <div
              className="rowSliderItem1"
              style={{
                backgroundColor: "#5effb1",
                width: "128px",
                height: "128px",
              }}
            >
              <h1>1</h1>
            </div>
            <div
              className="rowSliderItem2"
              style={{
                backgroundColor: "#5effb1",
                width: "128px",
                height: "128px",
              }}
            >
              <h1>2</h1>
            </div>
            <div
              className="rowSliderItem3"
              style={{
                backgroundColor: "#5effb1",
                width: "128px",
                height: "128px",
              }}
            >
              <h1>3</h1>
            </div>
            <div
              className="rowSliderItem1"
              style={{
                backgroundColor: "#5effb1",
                width: "128px",
                height: "128px",
              }}
            >
              <h1>1</h1>
            </div>
            <div
              className="rowSliderItem2"
              style={{
                backgroundColor: "#5effb1",
                width: "128px",
                height: "128px",
              }}
            >
              <h1>2</h1>
            </div>
          </div>
        </div>
      </div>
      <button className="rowSliderButtonLeft" onClick={() => handleSwipe(-1)}>
        <svg
          width="12.5"
          height="96"
          viewBox="0 0 14 98"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
          className="rowSliderButtonLeftVector"
        >
          <path d="M13.5 97L1 49L13.5 1" stroke="black" />
        </svg>
      </button>
      <button className="rowSliderButtonRight" onClick={() => handleSwipe(1)}>
        <svg
          width="14"
          height="98"
          viewBox="0 0 14 98"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
          className="rowSliderButtonRightVector"
        >
          <path d="M0.5 97L13 49L0.5 1" stroke="black" />
        </svg>
      </button>
    </div>
  );
};

export default RowSlider;
