import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function DetailPage() {
  const [waterBrands, setWaterBrands] = useState([]);
  useEffect(() => {
    axios.get("http://3.39.164.17:8000/waterbrand/").then((response) => {
      setWaterBrands(response.data);
    });
  }, []);

  let params = useParams();

  let waterBrandId = params.waterBrandId;

  let waterBrand = waterBrands[waterBrandId - 1];

  return (
    <main style={{ padding: "1rem 0" }}>
      <div>
        <h2>{waterBrand.name}</h2>
        <img
          src={require(waterBrand.image_url)}
          alt={`${waterBrand.name} 이미지`}
          style={{ width: "250px" }}
        />
      </div>
    </main>
  );
}
